import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import {Button} from 'react-bootstrap';

export default class Vendor extends Component {
     constructor(props) {
        super(props);

        this.state = {
            vendor: this.props.match.params.id

        }
    }

    disp_pdt(e){
        this.props.history.push({
            pathname: "/vendor/disp_pdt/" + this.state.vendor
        })
    }

    add_pdt(e){
        this.props.history.push({
                pathname: "/vendor/add_pdt/" + this.state.vendor
            })
    }

    todispatch_pdt(e){
        this.props.history.push({
                pathname: "/vendor/todispatch_pdt/" + this.state.vendor
            })
    }

    dispatched_pdt(e){
        this.props.history.push
                        ({
                            pathname: "/vendor/dispatched_pdt/" + this.state.vendor
                        })
    }
    render() {
        return (
            <div>
                <Button variant="secondary" size="lg" disabled block>
                    Vendor - {this.state.vendor}
                </Button>
                <Button variant="outline-secondary" size="lg" disabled block>
                    Vendor - {this.state.vendor}, Select Action for Products:
                </Button>
                <Button variant="primary" onClick={(e)=>this.disp_pdt(e)}>
                    View
                </Button>
                <Button variant="success" onClick={(e)=>this.add_pdt(e)}>
                    Add
                </Button>
                <Button variant="warning" onClick={(e)=>this.todispatch_pdt(e)}>
                    Dispatch
                </Button>
                <Button variant="danger" onClick={(e)=>this.dispatched_pdt(e)}>
                    Dispatched
                </Button>
            </div>

        )
    }
}
